import axios from "axios";

const BASE_URL = "http://localhost:8000/api/hubspot";
const HUBSPOT_CLIENT_ID = process.env.HUBSPOT_CLIENT_ID;
const HUBSPOT_REDIRECT_URI = process.env.HUBSPOT_REDIRECT_URI;

// export const authorizeHubSpot = async (userId, orgId) => {
//   try {
//     const response = await axios.get(`${BASE_URL}/authorize`, {
//       params: { user_id: userId, org_id: orgId },
//     });
//     window.location.href = response.data.url;
//   } catch (error) {
//     console.error("Error during HubSpot authorization:", error);
//   }
// };
export const authorizeHubSpot = async (userId, orgId) => {
    try {
      const url = `https://app.hubspot.com/oauth/authorize?client_id=${HUBSPOT_CLIENT_ID}&redirect_uri=${HUBSPOT_REDIRECT_URI}&scope=contacts`;
      
      // Send a response to frontend with the HubSpot authorization URL
      return url;  // This will send the URL to the frontend
    } catch (error) {
      console.error("Error during HubSpot authorization:", error);
      throw new Error("Authorization failed");
    }
  };

export const getHubSpotItems = async (userId, orgId) => {
  try {
    const response = await axios.get(`${BASE_URL}/items`, {
      params: { user_id: userId, org_id: orgId },
    });
    return response.data.items;
  } catch (error) {
    console.error("Error fetching HubSpot items:", error);
    return [];
  }
};
